import React, { useState } from "react";
import { motion } from "framer-motion";

const Filters = ({ data, setFilteredData }) => {
  const [filters, setFilters] = useState({ city: "", state: "", modelYear: "" });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });

    // Filter data based on the selected filters
    const filtered = data.filter((item) => {
      return (
        (value ? item[name].toString().toLowerCase().includes(value.toLowerCase()) : true) &&
        (filters.city ? item.City.toLowerCase().includes(filters.city.toLowerCase()) : true) &&
        (filters.state ? item.State.toLowerCase().includes(filters.state.toLowerCase()) : true) &&
        (filters.modelYear ? item["Model Year"].toString() === filters.modelYear : true)
      );
    });
    setFilteredData(filtered);
  };

  return (
    <div className="bg-white p-4 rounded shadow">
      <h2 className="text-xl font-bold mb-4">Filters</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div>
          <label className="block font-bold mb-2">City</label>
          <input
            type="text"
            name="city"
            value={filters.city}
            onChange={handleChange}
            className="w-full border rounded p-2"
            placeholder="Enter city"
          />
        </div>
        <div>
          <label className="block font-bold mb-2">State</label>
          <input
            type="text"
            name="state"
            value={filters.state}
            onChange={handleChange}
            className="w-full border rounded p-2"
            placeholder="Enter state"
          />
        </div>
        <div>
          <label className="block font-bold mb-2">Model Year</label>
          <input
            type="number"
            name="modelYear"
            value={filters.modelYear}
            onChange={handleChange}
            className="w-full border rounded p-2"
            placeholder="Enter model year"
          />
        </div>
      </div>
    </div>
  );
};

export default Filters;
