import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import './Map.css'; // Add a CSS file for styles

const Map = ({ data }) => {
  const parseLocation = (location) => {
    const match = location.match(/POINT \(([-\d.]+) ([-\d.]+)\)/);
    return match ? [parseFloat(match[2]), parseFloat(match[1])] : null;
  };
  const center = {
    lat: parseFloat(data[0].VehicleLocation.split(" ")[1]),
    lng: parseFloat(data[0].VehicleLocation.split(" ")[0]),
  };

  return (
    <div className="map-container"> {/* Wrap MapContainer with a div */}
      <MapContainer center={center} zoom={8} className="leaflet-map">
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; OpenStreetMap contributors"
        />
        {data.map((item, index) => {
          const coords = parseLocation(item["Vehicle Location"]);
          return (
            coords && (
              <Marker key={index} position={coords}>
                <Popup>
                  {item.Make} {item.Model} - {item.City}, {item.State}
                </Popup>
              </Marker>
            )
          );
        })}
      </MapContainer>
    </div>
  );
};

export default Map;
