import React, { useState } from "react";
import RangeChart from "./Charts/RangeChart";
import Filters from "./Filters";
import data from "./data/evData.json";

const Dashboard = () => {
  const [filteredData, setFilteredData] = useState(data);

  const handleFilterChange = (filters) => {
    let newData = data;
    if (filters.make) {
      newData = newData.filter((item) => item.Make === filters.make);
    }
    if (filters.year) {
      newData = newData.filter((item) => item["Model Year"] === filters.year);
    }
    if (filters.cafv) {
      newData = newData.filter((item) => item["Clean Alternative Fuel Vehicle (CAFV) Eligibility"] === filters.cafv);
    }
    setFilteredData(newData);
  };

  return (
    <div className="dashboard">
      <div className="container mx-auto p-4">
        <header className="text-center mb-6">
          <h1 className="text-3xl font-bold">EV Dashboard</h1>
        </header>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Filter Section */}
          <div className="col-span-1">
            <Filters onFilterChange={handleFilterChange} data={data} />
          </div>

          {/* Chart Section */}
          <div className="col-span-2">
            <RangeChart data={filteredData} />
          </div>
        </div>

        {/* Vehicle Data Table */}
        <VehicleTable data={filteredData} />
      </div>
    </div>
  );
};

export default Dashboard;
