import React from "react";
import { Chart, registerables } from "chart.js";
import { Bar } from "react-chartjs-2";

// Register Chart.js components
Chart.register(...registerables);

const RangeChart = ({ data }) => {
  const ranges = data.map((item) => item["Electric Range"]);

  // Group ranges into bins
  const bins = [0, 50, 100, 150, 200, 250, 300];
  const rangeCounts = bins.map((bin, index) =>
    ranges.filter((range) => {
      if (index === bins.length - 1) return range >= bin; // Last bin includes all higher values
      return range >= bin && range < bins[index + 1];
    }).length
  );

  const chartData = {
    labels: ["0-50", "50-100", "100-150", "150-200", "200-250", "250-300", "300+"],
    datasets: [
      {
        label: "Electric Range (miles)",
        data: rangeCounts,
        backgroundColor: "#4caf50",
      },
    ],
  };

  return (
    <div className="bg-white p-4 rounded shadow">
      <h2 className="text-xl font-bold mb-4">Electric Range Distribution</h2>
      <Bar data={chartData} />
    </div>
  );
};

export default RangeChart;
